import { Component, OnInit } from '@angular/core';
import { dataService } from '../data.service';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css']
})
export class CustomerComponent implements OnInit {
  customer: any[] | undefined;

  constructor(private dataService: dataService) {}

  ngOnInit() {
    this.dataService.getData().subscribe((data: { customer: any[] | undefined; }) => {
      this.customer = data.customer;
    });
  }
}

<p>customer works!</p>
<!-- customer.component.html -->

<div class="container">
  <h1>Customers</h1>
  <ul class="customer-list">
    <li *ngFor="let customer of customer | slice: startIndex:endIndex">
      {{ customer.name }}
      <ul class="order-list">
        <li *ngFor="let order of customer.orders">{{ order.name }}</li>
      </ul>
    </li>
  </ul>
  <div class="pagination">
    <button (click)="previousPage()" [disabled]="currentPage === 0">Previous</button>
    <button (click)="nextPage()" [disabled]="endIndex >= customer.length">Next</button>
  </div>
</div>
